<?phperror_reporting(E_ALL);include "database.php";//Connexion à la base$connexion = mysql_connect($db['default']['hostname'], $db['default']['username'], $db['default']['password']);if (!$connexion) die('Could not connect: ' . mysql_error());mysql_select_db($db['default']['database']);//Récupération de toutes les images qui commencent par 118_$repertoire_original = '../fichiers/user/avatars';if($dossier = opendir($repertoire_original)){	while(false !== ($nom_fichier = readdir($dossier)))	{		//Récupération de l'id (numéro après le _) -> on n'utilise que les avatars 118x118		$id = substr($nom_fichier, strpos($nom_fichier, '_') + 1, strpos($nom_fichier, '.') - (strpos($nom_fichier, '_') + 1));		echo $id.": ";		//Création du dossier de l'user s'il n'existe pas déjà		$repertoire = '../fichiers/user/'.$id;		if (!is_dir($repertoire)) mkdir($repertoire);				//Ajout de l'image		$fichier_source = $repertoire_original."/".$nom_fichier;		$fichier_destination = $repertoire."/".$nom_fichier;				copy($fichier_source, $fichier_destination);		echo $fichier_source;echo " -> ";echo $fichier_destination;echo "\n";		//Insertion dans la table user de l'image de l'avatar		$requete = "update user set avatar = ".$nom_fichier." where id = ".$id.";";		echo $requete;		echo(mysql_query($requete));echo "\n";	}}?>