<?php$db['default']['hostname'] = 'localhost';$db['default']['username'] = 'root';$db['default']['password'] = '';$db['default']['database'] = 'witty_dev';//Connexion à la base$connexion = mysql_connect($db['default']['hostname'], $db['default']['username'], $db['default']['password']);if (!$connexion) die('Could not connect: ' . mysql_error());mysql_select_db($db['default']['database']);//Récupération des flux$requete = "select id, old_password from user where old_password is not null";$resultat = mysql_query($requete);while($row = mysql_fetch_assoc($resultat)){		echo $row['id']."\n";	$salt = base_convert(sha1(uniqid(mt_rand(), true)), 16, 36); //Génération du salt	$new_password = crypt($row['old_password'], '$2y$07$'.$salt.'$');		$requete = "update user set password = '".$new_password."', salt = '".$salt."' where id = ".$row['id']." and old_password is not null";	print_r($requete);echo "\n";	print_r(mysql_query($requete));echo "\n";}echo 'ok';?>